const express=require("express"),cors=require("cors"),app=express(),api=require("./api/index.js");app.use(cors()),app.use("/",api);const port=5e3;app.use(express.json());const mysql=require("mysql"),connection=mysql.createConnection({host:"localhost",user:"root",password:"root"});connection.connect(),app.get("/bbs",(req,res)=>{connection.query("SELECT * from study.bbs",(function(err,rows,fields){if(err)throw err;res.send(rows),console.log(rows)}))}),app.delete("/bbs",(req,res)=>{const id=req.body.no;connection.query("delete from study.bbs where no=?",[id],(function(err,rows,fields){if(err)throw err;res.send(rows),console.log(rows)}))}),app.listen(5e3,()=>{console.log("Example app listening at http://localhost:5000")});