fetch("https://jsonplaceholder.typicode.com/todos").then(res=>res.json()).then(res=>{todos=res,list(),check()});let todos=[];function list(){console.log(todos);const ul=document.querySelector("ul"),li=document.querySelector("li"),todoMap=todoslist=>`<li class='todo-list ${!1!==todoslist.completed?"completed":""}'>\n          <div class="checkBox"><input type='checkbox' id='chkList${todoslist.id}'\n          ${!1!==todoslist.completed?"checked":""}>\n          <label for='chkList${todoslist.id}'></label></div>\n          <div class="todo-title">${todoslist.title}</div>\n          <button class="btndel">x</button></li>`;ul.innerHTML=todos.map(todoMap).join("");const btnDel=document.querySelectorAll(".btndel");for(let i=0;i<btnDel.length;i++)btnDel[i].addEventListener("click",e=>{todos.splice(i,1),e.target.parentNode.remove(),console.log(todos)})}function check(){const check=document.querySelectorAll("input[type='checkbox']");for(let i=0;i<check.length;i++)check[i].addEventListener("click",e=>{const eli=e.target.closest("li");if(!0!==check[i].checked)return eli.classList.remove("completed"),todos[i].completed=!1,void console.log(todos[i].completed);eli.classList.add("completed"),todos[i].completed=!0,console.log(todos[i].completed)})}document.querySelector("#add-btn").addEventListener("click",e=>{if(e.target.classList.contains("add")){const input=document.querySelector(".todo-txtInput");let id=0!==todos.length?Number(todos[todos.length-1].id)+1:1;if(0==input.value)return alert("내용을 입력하세요");todos.push({id:id,title:input.value,completed:!1}),list(),check()}});